return items
  .map(item => {
    const post = item.json;

    const titleRaw = post.title || 'untitled';
    const title = titleRaw
      .replace(/[^\w\s-]/g, '')      // Sonderzeichen raus
      .replace(/\s+/g, '_')          // Leerzeichen zu _
      .substring(0, 60);             // max 60 Zeichen

    const videoUrl = post.url_overridden_by_dest || post.url || null;

    return {
      json: {
        safeTitle: title,
        videoUrl,
        is_video: post.is_video === true // explizit als bool
      }
    };
  })
  .filter(item => item.json.is_video && item.json.videoUrl?.includes('v.redd.it'));

return items
  .map(item => {
    const post = item.json;
    return {
      title: post.title,
      reddit_video: post.media?.reddit_video?.fallback_url || null,
      is_video: post.is_video
    };
  })
  .filter(post => post.is_video && post.reddit_video)
  .map(post => ({
    json: {
      title: post.title,
      videoUrl: post.reddit_video
    }
  }));

return items.map(item => {
  const title = item.json.title
    .replace(/[^\w\s-]/g, '')
    .replace(/\s+/g, '_')
    .substring(0, 60);

  return {
    json: {
      videoUrl: item.json.videoUrl,
      safeTitle: title,
    }
  };
});

