// Hole alle Titel aus der Datenbank
const dbItems = $('Get-DB-Data').all();
// Extrahiere nur die Titeltexte und bereinige sie (entferne Dateiendungen)
const dbTitles = dbItems.map(item => {
  // Extrahiere den Titel und entferne die Dateiendung (.mp4, etc.)
  let title = item.json.title;
  // Entferne Dateiendungen wie .mp4, .avi, etc.
  return title.replace(/\.[^.]+$/, '');
});

// Hole alle Videos aus dem vorherigen Node
const videos = $('Filter Items').all();

// Filtere die Videos, deren safeTitle nicht in der Datenbank ist
const filteredVideos = videos.filter(video => {
  const safeTitle = video.json.safeTitle;

  // Pr√ºfe, ob ein √§hnlicher Titel in der Datenbank existiert
  const exists = dbTitles.some(dbTitle => {
    // Vergleiche den bereinigten DB-Titel mit dem safeTitle
    return dbTitle.includes(safeTitle) || safeTitle.includes(dbTitle);
  });

  // Wenn der Titel nicht existiert, behalte das Video
  return !exists;
});

// Gib die gefilterten Videos zur√ºck
return filteredVideos;

return items
  .map(item => {
    const output = item.json.stdout;

    // Nur final gemergte Datei verwenden!
    const match = output.match(/Merging formats into "(.+?\.mp4)"/);

    if (!match) return null;

    const path = match[1].trim();
    return {
      json: {
        videoPath: path,
        safeTitle: path.split("/").pop().replace(".mp4", "")
      }
    };
  })
  .filter(Boolean);

// Eingabe: FFmpeg-Execute-Node mit JSON-Array von Ergebnissen
const ffmpegResults = items.map(item => item.json);

// Regex zur Extraktion des Output-Pfads
const outputRegex = /Output #0, mp4, to '(.*?)':/;

// Ergebnisse sammeln
const outputPaths = ffmpegResults.map(result => {
  const match = result.stderr.match(outputRegex);
  return {
    original: result,
    path: match ? match[1] : null,
  };
});

// Filtere nur g√ºltige Ergebnisse mit Pfad
const valid = outputPaths.filter(entry => entry.path !== null);

// R√ºckgabeformat: z.B. f√ºr sp√§tere Verwendung in Merge-Node
return valid.map(entry => ({
  json: {
    outputPath: entry.path,
  },
}));

const diskItems = $items('Read/Write Files from Disk');
const dbItems = $items('Filter-against-DB');

return diskItems.map((diskItem) => {
  const filename = diskItem.json.fileName || '';
  const cleanFilename = filename.toLowerCase().replace(/[_\-\.]/g, '');

  // finde den ersten safeTitle, der im (vereinfachten) Dateinamen enthalten ist
  const match = dbItems.find((dbItem) => {
    const safeTitle = (dbItem.json.safeTitle || '').toLowerCase().replace(/[_\-\.]/g, '');
    return cleanFilename.includes(safeTitle);
  });

  return {
    json: {
      fileName: filename,
      videoUrl: match?.json.videoUrl ?? null,
      safeTitle: match?.json.safeTitle ?? 'NO_MATCH',
    },
  };
});

// Die ID kommt vom ersten Item
const mergeId = $items('Generate Merge ID')[0].json.mergeId;
// Hol die LLM-Ausgabe mit den ausgew√§hlten Video-IDs
const llmOutput = $items('AI Agent1')[0].json.output;
// Extrahiere die ausgew√§hlten Video-IDs und die vorgeschlagene Reihenfolge
const selectedIds = llmOutput.suggested_order || llmOutput.video_ids;
// Hole alle Video-Daten
const allVideos = $items('Get-Video-Infos');
// Filtere die Videos basierend auf den ausgew√§hlten IDs und behalte die Reihenfolge bei
const selectedVideos = selectedIds.map(id => {
  // Finde das Video mit der passenden ID
  const video = allVideos.find(item => item.json.id === id);
  return video ? video.json.minio_url : null;
}).filter(url => url !== null); // Entferne null-Werte f√ºr den Fall, dass ein Video nicht gefunden wurde

return [
  {
    json: {
      video_urls: selectedVideos,
      video_ids: selectedIds, // Wichtig: Wir geben die IDs weiter
      id: mergeId,
      compilation_title: llmOutput.compilation_title,
      social_media_description: llmOutput.social_media_description,
      hashtags: llmOutput.hashtags,
      theme: llmOutput.theme
    }
  }
];

function generateUUID() {
  const timestamp = Date.now().toString(36);
  const random = Math.random().toString(36).substring(2, 10);
  return `${timestamp}-${random}`;
}

return [
  {
    json: {
      mergeId: generateUUID()
    }
  }
];

// Extract the array of URLs
const urls = items[0].json["video_urls"] || [];

// Map URLs into the required structure
const formattedURLs = urls.map(url => ({ "video_url": url }));

// Return the transformed data
return [{
    json: {
        "video_urls": formattedURLs
    }
}];

// Hole alle input items
const input = items;

// Extrahiere alle IDs aus Feldern wie: { "output.video_ids": 54 }
return input
  .filter(item => item.json['output.video_ids'] !== undefined)
  .map(item => ({
    json: {
      rowId: item.json['output.video_ids'],
    },
  }));

return items.map(item => {
  const originalUrl = item.json.response;
  const minioUrl = originalUrl.replace('http://192.168.100.10:9768/', 'https://minio.plex123.uk/');

  return {
    json: {
      ...item.json,
      minio_url: minioUrl
    }
  };
});

const workflowStaticData = $getWorkflowStaticData('global');

// inititate the execution index if it desn't exist or increment if does
workflowStaticData.executionIndex = 
  !workflowStaticData?.executionIndex ? 1 :  // [bug] replace 0 with 1
  workflowStaticData.executionIndex + 1;

return [
  {
      data: $input.all(),
      executionIndex: workflowStaticData.executionIndex
  }
];
